import logo from './logo.svg';
import React, { useState } from 'react';
import {Bar} from 'react-chartjs-2';
import {Line} from 'react-chartjs-2'
import Chart from 'react-chartjs-2'
import dataInit from './producao.json';


function renderChart(data, labels, type) {

  var dataSum = somar(data, labels)
  var dataMean = media(data, labels, dataSum)
  var dados, graph, line, color

  var ctx = document.getElementById("myChart").getContext('2d');

  if (type != 'compare') {
      type == 'sum' ? (dados = dataSum) && (graph = 'line') && (line = 'Produção / Dia') && (color = 'rgba(75, 192, 192, 0.6)') :
          (dados = dataMean) && (graph = 'bar') && (line = 'Tempo médio de inatividade / Dia') && (color = 'rgba(150, 74, 237, 0.4)')

      let myChart = new Chart(ctx, {
          type: graph,
          data: {
              labels: labels,
              datasets: [{
                  label: line,
                  data: dados,
                  borderColor: color,
                  backgroundColor: color,
              }]
          },
          options: {
              layout: {
                  padding: {
                      left: 0,
                      right: 0,
                      top: 25,
                      bottom: 15
                  }
              }
          }
      });
  } else {

      var mixedChart = new Chart(ctx, {
          type: 'line',
          data: {
              labels: labels,
              datasets: [{
                  label: 'Produção / Dia',
                  yAxisID: 'A',
                  data: dataSum,
                  borderColor: 'rgba(75, 192, 192, 1)',
              }, {
                  label: 'Tempo médio de inatividade / Dia',
                  yAxisID: 'B',
                  data: dataMean,
                  borderColor: 'rgba(150, 74, 237, 1)',
              }]
          },
          options: {
              scales: {
                  yAxes: [{
                      id: 'A',
                      type: 'linear',
                      position: 'left',
                  }, {
                      id: 'B',
                      type: 'linear',
                      position: 'right',
                      ticks: {
                          max: 8,
                          min: 0
                      }
                  }]
              }
          }
      });
  }
}

function initial(type) {
  renderBtn(type)
}

function renderBtn(type) {

  var xmlhttp = new XMLHttpRequest();
  var url = "app/data/producao.json";

  xmlhttp.onreadystatechange = function() {
      if (this.readyState == 4 && this.status == 200) {
          var myArr = JSON.parse(this.responseText);
          var labels = ["17", "18", "19", "20", "21", "22"];
          renderChart(myArr, labels, type);
      }
  };
  xmlhttp.open("GET", url, true);
  xmlhttp.send();
}

function somar(data, labels) {
  let sum = [1, 1, 1, 1, 1, 1];
  for (let i = 0; i < data.length; i++) {
      for (let j = 0; j < labels.length; j++) {
          if (JSON.stringify(data[i].pt).includes("2016-04-" + labels[j])) {
              sum[j]++;
          }
      }
  }
  return sum;
}

function media(data, labels, somaDias) {
  let mean = [0, 0, 0, 0, 0, 0];
  let s = 0;

  for (let i = 0; i < labels.length; i++) {
      let sum = 0;
      for (let j = 1; j < somaDias[i]; j++) {
          sum += data[s++].sis;
      }
      mean[i] = sum / somaDias[i];
  }
  return mean;
}

function Icons(){
  return(
    <ul class="icons">
          <li><a href="https://www.linkedin.com/in/anna-puga/" class="icon brands fa-linkedin"><span class="label">Facebook</span></a></li>
          <li><a href="https://www.instagram.com/annxrchism/?hl=pt-br" class="icon brands fa-instagram"><span class="label">Instagram</span></a></li>
          <li><a href="https://github.com/annxpugx" class="icon brands fa-github"><span class="label">GitHub</span></a></li>
    </ul>
  )
}

function Contact(){
  return(
    <article>
      <h2 class="major">Contato</h2>
      <form method="post" action="#">
          <div class="fields">
              <div class="field half">
                  <label for="name">Anna Puga Campos Rodrigues</label>
              </div>
              <div class="field half">
                  <label for="email">annapugac@gmail.com</label>
                  <label for="email">(031)99974-4494</label>
              </div>
              <div class="field">
                  <h2 class="major">Sobre mim</h2>
                  <label for="message">Curso atualmente o 3º período de Ciência da Computação da PUC Minas. 
                      Morei cerca de um ano na França em busca de aperfeiçoamento da língua francesa e tive experiências de trabalho voluntário durante esse 
                      período. <a></a></label>
                  <label for="message">Inglês e Francês avançados, Espanhol intermediário. <a></a></label><br/>
                  <h2 class="major">Projetos Pessoais</h2>
                  <label for="message">Site A.DOTE (2020/2): </label>
                  <label> plataforma para assistência de famílias no processo adotivo, simplificando as etapas e a burocracia através de informações e funcionalidades para organização.
                      Nesse projeto, utilizo a Spark Framework para o Backend,
                      JS, HTML e CSS para o Frontend e PostgreSQL para o banco de dados.
                      </label><br/>
                  <h2 class="major">tecnologias</h2>
                  <label for="message"/>
                      <ul>
                          <li>C/C++</li>
                          <li>Java</li>
                          <li>JavaScript</li>
                          <li>SQL</li>
                      </ul>
              </div>
          </div>
      </form>
      <Icons></Icons>      
    </article>
  )
}

function About(){
    return (
      <article id="intro">
        <h2 class="major">Sobre</h2>
        <p>Os dados recolhidos são provenientes da produção de uma fábrica ao longo de uma semana.</p>
        <p>O desafio propõe que o participante faça uma análise exploratória dos dados e gere gráficos para visualização da produção diária de uma das máquinas. Além do que foi pedido, nesta aplicação há um gráfico extra que calcula a média do tempo
            gasto entre produções consecutivas todos os dias utilizando o campo sis (speed in seconds), a fim de verificar se houve perda de produtividade devido à lentidão da máquina e, também, se esta média tem tendência de crescimento ao longo
            da semana.
        </p>
      </article>
    )
}

function Container(){
  return(
      <div class="container" id="container">
          <canvas id="myChart"></canvas>
      </div>
  )
}

function Graphs(){

  const [showContainer, setShowContainer] = useState(false);

  return (
    <article id="work">
      <h2 class="major" id="titleMajor">Gráficos</h2>
      <section id="initialBTN">
          <button onClick={() => {setShowContainer(true); initial('sum')}}>Ver Gráficos</button>
      </section>
        {showContainer && <Container></Container>}
      <p id="explicacaoGrafico"><br/> <br/> Para as seguintes visualizações foi utilizada a biblioteca chart.js .</p>
    </article>
  )
}

function Footer(){

  return(
    <footer id="footer">
        <p class="copyright">&copy; Desafio Indwise - Anna Puga.</p>
    </footer>
  )
}

function Header(){

  return(
    <section>
      <div class="content">
          <div class="inner">
              <h1>Desafio Indwise</h1>
              <p> análise exploratória de dados</p>
          </div>
      </div>
    </section>
  )
}

function Logo(){
  return (
    <div class="logo">
        <span class="icon fa-chart-bar"></span>
    </div>
  )
}

function Show (){

  const [showHeader, setShowHeader] = useState(true);
  const [showAbout, setShowAbout] = useState(false);
  const [showGraphs, setShowGraphs] = useState(false);
  const [showContact, setShowContact] = useState(false);

  return (
          <header id="header">

              {showHeader && <Logo></Logo>}

              {showHeader && <Header></Header>}

              {showAbout && <About></About>}

              {showGraphs && <Graphs></Graphs>}

              {showContact && <Contact></Contact>}


              <nav>
                    <ul>
                        <li><a onClick = {() => {
                          setShowAbout(false); setShowHeader(true); setShowGraphs(false); setShowContact(false);}
                        }>Home</a></li>
                        <li><a onClick = {() => {
                          setShowAbout(true); setShowHeader(false); setShowGraphs(false); setShowContact(false);}
                        }>Sobre</a></li>
                        <li><a onClick = {() => {
                          setShowGraphs(true); setShowHeader(false); setShowContact(false); setShowAbout(false);}
                        }>Gráficos</a></li>
                        <li><a onClick = {() => {
                          setShowContact(true); setShowHeader(false); setShowGraphs(false); setShowAbout(false);}
                        }>Contato</a></li>
                    </ul>
              </nav>

          </header>   
  )
}

function App() {

  const [show, setShow] = useState(false);

  return (
    <div className="App">

      <div id="wrapper">

        <Show></Show>

        <Footer></Footer>

      </div>

      <div id="bg"></div>

      <script src="assets/js/jquery.min.js"></script>
      <script src="assets/js/browser.min.js"></script>
      <script src="assets/js/breakpoints.min.js"></script>
      <script src="assets/js/util.js"></script>
      <script src="assets/js/main.js"></script>

    </div>

  );
}

export default App;
