import React from 'react';
import {Line} from 'react-chartjs-2';
import myData from "./producao.json";
import logo from './logo.svg';
export {Graph}

function somar() {
    let sum = [1, 1, 1, 1, 1, 1];
    for (let i = 0; i < myData.length; i++) {
        for (let j = 0; j < labels.length; j++) {
            if (JSON.stringify(myData[i].pt).includes("2016-04-" + labels[j])) {
                sum[j]++;
            }
        }
    }
    return sum;
}


const labels = ['17', '18', '19','20', '21', '22'];

const state = {
  labels: labels,
  datasets: [
    {
      label: 'Produção',
      fill: false,
      lineTension: 0.5,
      backgroundColor: 'rgba(75,192,192,1)',
      borderColor: 'rgba(0,0,0,1)',
      fontColor: 'white',
      scaleFontColor: "#ff0000",
      borderWidth: 2,
      data: somar()
    }
  ],
    options: {
        layout: {
            padding: {
                left: 0,
                right: 0,
                top: 25,
                bottom: 15
            }
        }
    },
}

export default class Graph extends React.Component {
  render() {
    return (
      <div>
        <Line
          data={state}
          options={{
            title:{
              display:true,
              text:'PRODUÇÃO DIÁRIA',
              fontSize:20,
              fontColor:  'rgba(96, 94, 89, 7)',
              fontWeight: 600,
              fontFamily: "Source Sans Pro", 
              letterSpacing: '1.6px',
              textTransform: 'uppercase'
            },
            legend:{
              display:true,
              position:'right',
              fontColor: 'rgba(75,192,192,1)'
            }
          }}
        />
      </div>
    );
  }
}
