import React from 'react';
import {Line} from 'react-chartjs-2';
import myData from "./producao.json";
import logo from './logo.svg';
export {Graph}

function somar() {
    let sum = [1, 1, 1, 1, 1, 1];
    for (let i = 0; i < myData.length; i++) {
        for (let j = 0; j < labels.length; j++) {
            if (JSON.stringify(myData[i].pt).includes("2016-04-" + labels[j])) {
                sum[j]++;
            }
        }
    }
    return sum;
}


const labels = ['17', '18', '19','20', '21', '22'];

const state = {
  labels: labels,
  datasets: [
    {
      label: 'Produção',
      fill: true,
      lineTension: 0.5,
      backgroundColor: 'rgba(75,192,192,0.4)',
      borderColor: 'rgba(75, 192, 192, 0.6)',
      fontColor: 'white',
      scaleFontColor: "#ff0000",
      borderWidth: 2,
      data: somar()
    }
  ],
    options: {
        layout: {
            padding: {
                left: 0,
                right: 0,
                top: 25,
                bottom: 25
            }
        }
    },
}

export default class Graph extends React.Component {
  render() {
    return (
      <div style={{position: "relative", height:40, width:80}}>
        <Line
          data={state}
          options = {{
            responsive: true,
            title:{
              display: true,
              text:'PRODUÇÃO DIÁRIA',
              fontSize:40,
              fontColor:'rgba(245, 240, 228, 0.7)',
              fontWeight: 600,
              fontFamily: "inherit", 
              letterSpacing: '2.6px',
              textTransform: 'uppercase'
            },
            scales: {
              yAxes: [{
                ticks: {
                  fontColor: 'rgba(245, 240, 228, 0.7)'
                }
                
              }],
              xAxes: [{
                ticks: {
                  fontColor: 'rgba(245, 240, 228, 0.7)'
                }
                
              }]
            },
            legend:{
              display:true,
              position:'top',
              labels: {
                fontColor: 'rgba(245, 240, 228, 0.7)',
                fontSize: 25,    
                fontFamily: "inherit"
              }
            }
          }}
        />
      </div>
    );
  }
}
