import Chart from 'react-chartjs-2';
import { myData } from "./producao.json";
Chart.defaults.global.defaultFontFamily = "Roboto, sans-serif";
const labels = ["17", "18", "19", "20", "21", "22"];

function somar() {
    let sum = [1, 1, 1, 1, 1, 1];
    for (let i = 0; i < myData.length; i++) {
        for (let j = 0; j < labels.length; j++) {
            if (JSON.stringify(myData[i].pt).includes("2016-04-" + labels[j])) {
                sum[j]++;
            }
        }
    }
    return sum;
}

// LineChart
class LineChart extends React.Component {
  constructor(props) {
    super(props);
    this.canvasRef = React.createRef();
  }

  componentDidUpdate() {
    this.myChart.data.labels = labels;
    this.myChart.data.data = myData;
    this.myChart.update();
  }

  componentDidMount() {
    this.myChart = new Chart(this.canvasRef.current, {
        type: 'line',
        data: {
            labels: labels,
            datasets: [{
                label: 'Produção / Dia',
                yAxisID: 'A',
                data: dataSum,
                borderColor: 'rgba(75, 192, 192, 1)',
            }, {
                label: 'Tempo médio de inatividade / Dia',
                yAxisID: 'B',
                data: dataMean,
                borderColor: 'rgba(150, 74, 237, 1)',
            }]
        },
        options: {
            scales: {
                yAxes: [{
                    id: 'A',
                    type: 'linear',
                    position: 'left',
                }, {
                    id: 'B',
                    type: 'linear',
                    position: 'right',
                    ticks: {
                        max: 8,
                        min: 0
                    }
                }]
            }
        }
    });
  }

  render() {
    return <canvas ref={this.canvasRef} />;
  }
}

class Graph extends React.Component {
    constructor(props) {
      super(props);
  
      this.state = {
        data: myData
      };
    }
  
    componentDidMount() {
      window.setInterval(() => {
        this.setState({
          data: myData
        })
      }, 5000)
    }
  
    render() {
      return (
        <div className="Graph">
          <div className="main chart-wrapper">
            <LineChart
              data={this.state.data.data}
              title={this.state.data.title}
              color="#3E517A"
            />
          </div>
        </div>
      );
    }
  }
